var request = require('request'),
    Promise = require('bluebird'),
    navigationUrl = require('../functions/constructNavigationUrl'),
    authenticate = require('./authenticate'),
	parseAssetRef = require('../functions/parseAssetRef');

module.exports = function (assetRef) {
    return new Promise(function (resolve, reject) {
        authenticate().then(function (ticket) {
            request({
                method: 'GET',
                headers: {
                    'accept': 'application/json;charset=utf-8',
                    'pragma': 'auth-redirect=false'
                },
				url: navigationUrl(parseAssetRef(assetRef.id).id),
                qs: {
                    multiticket: ticket, 
					depth: 4
                },
                json: true
            }, function (err, res, body) {
                if (err) reject(err);
                resolve(body);
            });
        });
    });
};
